import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import './App.css';
import HomePage from './pages/homepage/HomePage';
import ShopPage from './pages/shop-page/ShopPage';
import Navbar from './components/navbar/Navbar';
import { Switch, Route } from 'react-router-dom';
import SignInAndSignUp from './pages/sign-in-and-sign-up/SignInAndSignUp';
import Cookies from 'js-cookie';
import { setCurrentUser } from './redux/user/userActions'

function App() {

  const getCurrentUser = () => {
    const token = Cookies.get('appaShop');

    if (token) {

      fetch('http://localhost:3000/api/v1/get-user', {

        method: 'GET',

        headers: {
          Authorization: `Bearer ${token}`,
          Accept: 'application/json',
          'Content-type': 'application/json',
        },

      })
      .then( response => response.json())
      .then(
        userData => {
          console.log(userData)
        }
      );
    };

  };

  useEffect(() => {
    getCurrentUser();
  }, []);

  return (
    <div> 
      <Navbar />
      <Switch>
          <Route exact path='/' component={HomePage} />
          <Route path='/shop' component={ShopPage} />
          <Route path='/signin' component={SignInAndSignUp} />
      </Switch>
    </div>
  );
};

/*
  -mdp is a function with a dispatch arg, it returns an object with a key set to the
  name of the corrosponding imported action function 

   -the action key pairs to a function that takes in whatever value you intend to pass into
   your imported action function, it returns dispatch( yourAction(value) )

   - can now call props.yourAction(val) ie., setCurrentUser(userObject) to set/remove
   currentUser in the redux store.
*/
const mdp = (dispatch) => ({
  setCurrentUser: user => dispatch(setCurrentUser(user))
});

/*
  -msp is a function with a reduxState arg, it returns an object with a key set to the

*/
const msp = (reduxState) => ({ currentUser: reduxState.user.currentUser })
export default connect(msp, mdp)(App);
