interface PersistedItem {
    name: string;
    qty: number;
}
interface IncomingItem {
    id: number;
    name: string;
}
class ShoppingCart {
    items: { [id: number]: PersistedItem };
    constructor() {
        this.items = {};
    }

    addItem({ id, name }: IncomingItem): void {
        if (!this.items[id]) {
            this.items[id] = {
                qty: 1,
                name: name,
            };
        } else this.items[id].qty += 1;
    }

    removeItem(id: number): void {
        delete this.items[id];
    }

    mapToArray(callBack: (item: PersistedItem) => unknown): Array<unknown> {
        const result: Array<unknown> = [];
        for (const prop in this.items) {
            result.push(callBack(this.items[prop]));
        }
        return result;
    }
}

export default ShoppingCart;
