interface PersistedItem {
    name: string;
    qty: number;
}
interface IncomingItem {
    id: number;
    name: string;
}
interface Cart {
    [id: number]: PersistedItem;
}
class ShoppingCart {
    addItem(currentCart: Cart, { id, name }: IncomingItem): void {
        if(!currentCart[id]) {
            currentCart[id] = { name: name };
        }
    }

    removeItem(id: number): void {
        delete this.items[id];
    }

    mapToArray(callBack: (item: PersistedItem) => unknown): Array<unknown> {
        const result: Array<unknown> = [];
        for (const prop in this.items) {
            result.push(callBack(this.items[prop]));
        }
        return result;
    }
}

export default ShoppingCart;
