import ShoppingCart, { IncomingItem } from '../../utils/ShoppingCart';

const cart = new ShoppingCart();
const INIT_STATE = {
    hidden: true,
    currentCart: {},
};

export interface Action {
    type: string;
    payload?: IncomingItem;
}

export interface RootState {
    hidden?: true | false;
}

const cartReducer = function (prevState = INIT_STATE, action: Action): RootState {
    switch (action.type) {
        case 'TOGGLE_CART_DISPLAY':
            return {
                ...prevState,
                hidden: !prevState.hidden,
            };
        case 'ADD_ITEM':
            return cart.addItem(prevState.currentCart, action.payload);

        default:
            return prevState;
    }
};

export default cartReducer;
