export const toggleCartDisplay = function (): { type: string } {
    return {
        type: 'TOGGLE_CART_DISPLAY',
    };
};

export const addItem = function (item: unknown) : { type: string, payload: unknown} {

    return {
        type: 'ADD_CART_ITEM',
        payload: item,
    };
};
