interface PersistedItem {
    name: string;
    qty: number;
}
export interface IncomingItem {
    id: number;
    name: string;
}
interface Cart {
    [id: number]: PersistedItem;
}
class ShoppingCart {
    addItem(currentCart: Cart, { id, name }: IncomingItem): Cart {
        if (!currentCart[id]) {
            currentCart[id] = {
                name: name,
                qty: 1,
            };

            return { ...currentCart };
        } else {
            currentCart[id].qty += 1;
            return { ...currentCart };
        }
    }

    removeItem(currentCart: Cart, id: number): Cart {
        delete currentCart[id];
        return { ...currentCart };
    }
}

export default ShoppingCart;
